+  // Validation
+  validateStepData(stepId: string, data: any): ValidationResult;
+  checkDataRequirements(stepId: string): DataRequirement[];
+}
+
+enum ProcessStatus {
+  NOT_STARTED = 'not_started',
+  RUNNING = 'running',
+  PAUSED = 'paused',
