+```typescript
+// Generated from BUSY file
+export class ClientOnboardingProcess extends Process {
+  constructor() {
+    super({
+      name: "Client Onboarding",
+      layer: "L0",
+      estimatedDuration: "3 days",
+      description: "Complete client onboarding workflow"
