+
+    it('should stop process', async () => {
+      await process.start(mockContext);
+      
+      process.stop('Testing stop functionality');
+      expect(process.getState().status).toBe(ProcessStatus.CANCELLED);
+      expect(process.isRunning()).toBe(false);
